import Head from 'next/head'
import logoTitleImage from '../images/logo_title.png'
import Nav from "../components/Nav"
import homeImage from '../images/home_main.jpg'
import {Form, Formik, Field, ErrorMessage, formikHelpers} from 'formik'
import {object, string} from 'yup';

const initialValues = {
    email: '',
    password: ''
  }

function Login() {
    return (

        <div>
            <Head>
                <title>La Garchere - login</title>
                <link rel="icon" href={logoTitleImage} type="image/icon type"/>
            </Head>
            <Nav />
            <div className="container-login">
                <img className="bg-image" src={homeImage} alt="La GarchÃ¨re kamer"/>
                <div className="container-section">
                    <section className="section-login">
                        <h1 className="heading-style-2">Welkom!</h1>
                        <Formik 
                            validationSchema={
                                object({
                                email: string().required()
                                })
                            }
                            initialValues={initialValues} 
                            onSubmit={(values, formikHelpers)=> {
                                // axios.post("https://wdev.be/wdev_anneleen/eindwerk/api/", values)
                                // .then(function (response) {
                                // console.log(response);
                                // })
                                // .catch(function (error) {
                                // console.log(error);
                                // });

                            }}>
                                {({values, errors, isSubmitting}) => (
                                <Form>
                                    <Field name="email" type="email" placeholder="email"></Field>
                                    <div>
                                    <ErrorMessage name="email"></ErrorMessage>
                                    </div>
                                    <Field name="password" type="password" placeholder="wachtwoord"></Field>
                                    <div>
                                    <ErrorMessage name="password"></ErrorMessage>
                                    </div>
                                    <button type="submit" className="button-style-1" disabled={isSubmitting}>Inschrijven</button>
                                </Form>
                                )}
                            </Formik>
                    </section>
                </div>   
            </div>
            <style jsx>{`
                .container-login .bg-image {
                    width: 100vw;
                    height: 91vh;
                    object-fit: cover;
                    position: absolute;
                    z-index: -1;
                }

                .container-section {
                    width: 100vw;
                    height: 91vh;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                .section-login {
                    background-color: white;
                    width: 80vw;
                    height: 60vh;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;

                }

                input {
                    background-color: transparent;
                    padding: 10px 20px;
                    margin: 10px 0;
                    border: none;
                    border-bottom: 1px solid #1F1F1F;
                    font-family: 'Hind', sans-serif;
                }

                @media (min-width: 60em) {
                    .container-login .bg-image {
                        margin-top: -110px;
                        height: 100vh;
                    }
                }


            `}</style>
        </div>
    )
}

  export default Login
