import Layout from "../components/Layout"
import Head from 'next/head'
import logoTitleImage from '../images/logo_title.png'
import mainImage from '../images/contact_main.jpg'

const Contact = () => {
    return (
        <div>
            <Head>
                <title>La Garchere - Contact</title>
                <link rel="icon" href={logoTitleImage} type="image/icon type"/>
            </Head>
        <Layout>
            <div className="contact-container">
                <div className="container-main">
                    <img className="bg-image" src={mainImage} alt="La GarchÃ¨re"/>
                    <div className="main-block">
                        <h1 className="heading-style-2">Contacteer ons</h1>
                        <form>
                            <input type="text" placeholder="Voornaam"/>
                            <input type="text" placeholder="Achternaam"/>
                            <input type="email" placeholder="Email"/>
                            <input type="password" placeholder="Wachtwoord"/>
                            <textarea placeholder="Bericht"></textarea>
                            <button className="button-style-1">Verzenden</button>
                        </form>
                    </div>
                </div>
            </div>
        </Layout>
    <style jsx>{`
        .container-main {
            height: 100vh;
            margin: 0 auto;
            overflow: hidden;
            position: relative;
            top: -65px;
        }
        .bg-image {
            height: 100%;
            left: 50%;
            margin-left: -600px;
            position: absolute;
        }
        .main-block {
            padding: 40px;
            position:absolute;
            background-color: white;
            width: 70%;
            margin-left: auto;
            margin-right: auto;
            left: 0;
            right: 0;
            text-align: center;
            top: 110px;
        }

        form {
            display: flex;
            flex-direction: column;
            margin-top: 40px;
        }

        input, textarea {
            background-color: transparent;
            padding: 10px 20px;
            margin: 10px 0;
            border: none;
            border-bottom: 1px solid #1F1F1F;
            font-family: 'Hind', sans-serif;

        }

        button {
            width: 60%;
            margin: 20px auto 0 auto;
        }

        @media (min-width: 30em) {        
            .main-block {
                margin: 10%;
                width: auto;
                top: 50px;
            }
        }

        @media (min-width: 40em) {        
            .main-block {
                margin: 20%;
                width: auto;
                top: -30px;
            }
        }

        @media (min-width: 50em) {        
            .main-block {
                width: 320px;
                margin-top: 140px;
            }
            .main-block {
                margin-left: 50%;
            }
        }

        @media (min-width: 55em){
            .bg-image {
                min-height: 100%;
                left: 50%;
                margin-left: -500px;
                position: absolute;
            }
        }

        @media (min-width: 60em) {
            .container-main {
              top: -110px;
            }
            .bg-image {
                height: auto;
                left: 0;
                margin-left: 0;
                position: absolute;
                width: 100%;
            }
        }

    `}</style>
        </div>
    )
  }

  export default Contact
